sys_script.script:
(function executeRule(current, previous /*null when async*/) {

    var rightnow = new GlideDateTime(); // Pega data atual
    var whenNeeded = new GlideDateTime(current.u_when_needed); // Pega o campo onde é colocado a data

    // comparação se "whenNeeded" for antes da data atual, adiciona um erro e aborta a ação.
    if (whenNeeded.before(rightnow)) {
        gs.addErrorMessage("When needed date cannot be in the past.  Your request has not been saved to the database.");
        current.setAbortAction(true);

    }


    // compara se a data ainda é de hoje, impedindo que o usuario salve um arquivo que seja dentro do mesmo dia

    var today = rightnow.getLocalDate(); // Armazena o valor da variavel em uma nova variavel "today"
    var isToday = whenNeeded.getLocalDate(); // Armazena o valor que o campo está recebendo na variavel "isToday"

    // Faz a comparação
    if (today.compareTo(isToday) == 0) {
        gs.addErrorMessage("You cannot submit NeedIt requests for today.");
        current.setAbortAction(true);
    }

})(current, previous);
